// Utilities
import { defineStore } from 'pinia'
import { Format } from "@/types/format";

// Types


export const useFormatStore = defineStore('format', {
  state: () => ({
    selectedFormatId: 'Selecteer een formaat...' as string,

    _headerImage: 'data:image/jpeg;base64,' as string,
    _formats: [
      {
        id: 'a3_4x4',
        name: 'A3 4x4',
        orientation: 'portrait',
        unit: 'mm',
        format: [297, 420],
        width: 297,
        height: 420,
        headerHeight: 105,
        margin: 10,
        body: {
          margin: 10,
          rows: 4,
          columns: 4,
          titleFontSize: 18,
          subTitleFontSize: 14,
          salePriceFontSize: 48,
          priceFontSize: 18,
          lineHeight: 7,
          skewedBoxMargin: 7,
          skewedBoxAngle: 6,
          skewedBoxHeight: 30,
          skewedBoxYOffset: 7,
        }
      }
    ] as Format[]
  }),
  getters: {
    headerImage: (state): string => state._headerImage,
    format: (state): Format | undefined =>
      state._formats.find((format: Format): boolean =>
        format.id === state.selectedFormatId),
    availableFormats: (state): any[] =>
      state._formats.map((format: Format): any => {
        return {
          'title': format.name,
          'value': format.id,
        }
    }),
  },
})
